mapper.select:
SELECT USER_ID, U_PWD, U_NAME, U_NICKNAME, U_NUMBER, U_EMAIL, U_GENDER, U_STATUS, REG_DATE, U_MARRYDATE, U_POINT FROM (SELECT ROWNUM AS RNUM, USER_ID, U_PWD, U_NAME, U_NICKNAME, U_NUMBER, U_EMAIL, U_GENDER, U_STATUS, REG_DATE, U_MARRYDATE, U_POINT FROM (SELECT USER_ID, NVL(U_PWD, '') AS U_PWD, NVL(U_NAME, '') AS U_NAME, NVL(U_NICKNAME, '') AS U_NICKNAME, NVL(U_NUMBER, '') AS U_NUMBER, NVL(U_EMAIL, '') AS U_EMAIL, NVL(U_GENDER, '') AS U_GENDER, NVL(U_STATUS, '') AS U_STATUS, NVL(REG_DATE, '') AS REG_DATE, NVL(U_MARRYDATE, '') AS U_MARRYDATE, NVL(U_POINT, 0) AS U_POINT FROM WD_USER WHERE 1=1
AND USER_ID LIKE '%' || #{userId} || '%'
AND U_NAME LIKE '%' || #{userName} || '%'
AND U_STATUS = #{status}
ORDER BY REG_DATE DESC)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
