mapper.delete:
DELETE FROM WD_COMMENT
WHERE
        PARENT_SEQ = #{parentSeq}
    AND COMMENT_SEQ = #{commentSeq}

mapper.update:
UPDATE WD_COMMENT
SET
    WD_FBOARD_COMMENT = #{wdFBoardComment}
WHERE
        PARENT_SEQ = #{parentSeq}
    AND COMMENT_SEQ = #{commentSeq}
    AND USER_ID = #{userId}

mapper.delete:
DELETE FROM 
    WD_COMMENT
WHERE
    PARENT_SEQ = #{parentSeq}
AND 
    COMMENT_SEQ = #{commentSeq}
