mapper.select:
SELECT REZ_NO, NVL(WH_CODE,'')AS WH_CODE, NVL(H_CODE,'')AS H_CODE, NVL(S_CODE,'')AS S_CODE, NVL(DC_CODE,'')AS DC_CODE, NVL(D_NO,'')AS D_NO, NVL(M_CODE,'')AS M_CODE, NVL(M_PLUS_NUM,'')AS M_PLUS_NUM, NVL(REZ_STATUS,'N')AS REZ_STATUS, NVL(TO_CHAR(REZ_DATE,'YYYY.MM.DD HH24:MI:SS'), '')AS REZ_DATE, NVL(WDATE,'')AS WDATE, NVL(USER_ID,'')AS USER_ID, NVL(REZ_FULL_PRICE,0)AS REZ_FULL_PRICE, NVL(C_CODE,'')AS C_CODE, NVL(REZ_POINT,0)AS REZ_POINT, NVL(S_DATE,'')AS S_DATE FROM WD_REZ WHERE 1 = 1
AND USER_ID = #{userId}
AND REZ_NO = #{rezNo}
AND REZ_STATUS = #{rezStatus}

SELECT COUNT(REZ_NO) AS CNT FROM WD_REZ WHERE 1=1
AND REZ_STATUS = #{rezStatus}
AND USER_ID LIKE '%' || #{searchValue} || '%'
AND REZ_NO = #{searchValue}

mapper.update:
UPDATE WD_REZ
SET
    WH_CODE = #{whCode},
    H_CODE = #{hCode},
    WDATE = #{wDate}
WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

UPDATE WD_REZ
SET
    S_CODE = #{sCode},
    S_DATE = #{sDate}
WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

UPDATE WD_REZ
SET
    DC_CODE = #{dcCode},
    D_NO = #{dNo}

	,
	WDATE = #{wDate}

WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

UPDATE WD_REZ
SET
    M_CODE = #{mCode},
    M_PLUS_NUM = #{mPlusNum}

	,
	WDATE = #{wDate}

WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

UPDATE WD_REZ
SET
    REZ_DATE = SYSDATE,
    WDATE = #{wDate}
WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

UPDATE WD_REZ
SET M_CODE = '', M_PLUS_NUM = ''
WHERE REZ_NO = #{rezNo} AND USER_ID = #{userId}

UPDATE
    WD_REZ
SET
    REZ_DATE = SYSDATE,
    REZ_FULL_PRICE = #{rezFullPrice}

    ,
    C_CODE = #{cCode}

WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

mapper.select:
SELECT REZ_NO, WH_CODE, H_CODE, S_CODE, DC_CODE, D_NO, M_CODE, M_PLUS_NUM, REZ_STATUS, REZ_DATE, WDATE, USER_ID, REZ_FULL_PRICE, C_CODE, REZ_POINT, S_DATE FROM (SELECT ROWNUM AS RNUM, REZ_NO, WH_CODE, H_CODE, S_CODE, DC_CODE, D_NO, M_CODE, M_PLUS_NUM, REZ_STATUS, REZ_DATE, WDATE, USER_ID, REZ_FULL_PRICE, C_CODE, REZ_POINT, S_DATE FROM( SELECT REZ_NO, NVL(WH_CODE, '') AS WH_CODE, NVL(H_CODE, '') AS H_CODE, NVL(S_CODE, '') AS S_CODE, NVL(DC_CODE, '') AS DC_CODE, NVL(D_NO, '') AS D_NO, NVL(M_CODE, '') AS M_CODE, NVL(M_PLUS_NUM, 0) AS M_PLUS_NUM, NVL(REZ_STATUS, 'Y') AS REZ_STATUS, NVL(TO_CHAR(REZ_DATE, 'YYYY.MM.DD HH24:MI:SS'), '') AS REZ_DATE, NVL(WDATE, '') AS WDATE, USER_ID, NVL(REZ_FULL_PRICE, 0) AS REZ_FULL_PRICE, NVL(C_CODE, '') AS C_CODE, NVL(REZ_POINT, 0) AS REZ_POINT, NVL(S_DATE, '') AS S_DATE FROM WD_REZ WHERE 1=1
AND USER_ID LIKE '%' || #{searchValue} || '%'
AND REZ_NO = #{searchValue}
AND REZ_STATUS LIKE '%'|| #{rezStatus} || '%'
ORDER BY REZ_NO DESC)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE 
	WD_REZ

SET
    REZ_POINT = #{rezPoint}

WHERE
    REZ_NO = #{rezNo}

UPDATE WD_REZ
SET
   WDATE = #{wDate}
WHERE
         REZ_NO = #{rezNo}
    AND  REZ_STATUS = 'N'
    AND USER_ID = #{userId}
